import React, { useEffect, useState } from "react";
import Zoom from "react-medium-image-zoom";
import "react-medium-image-zoom/dist/styles.css";
import { useDispatch } from "react-redux";
import Swal from "sweetalert2";
import { api_url } from "../helpers";
import { changeOrderStatusAction } from "../redux/actions/adminAction";
import { Link } from "react-router-dom";

const ImagesModal = ({
  showmodal,
  toggle,
  invoice,
  name,
  modalName,
  imageUrl,
  status,
  recipes_id,
  user_id,
}) => {
  const [showModal, setShowModal] = useState(false);

  const dispatch = useDispatch();
  useEffect(() => {
    setShowModal(showmodal);
  }, [showmodal]);

  const confirmBtn = () => {
    Swal.fire({
      title: "Confirm this order?",
      text: "You won't be able to revert this",
      icon: "question",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Yes, confirm this order",
    }).then((result) => {
      if (result.isConfirmed) {
        Swal.fire("Order Confirmed!", "", "success");
        dispatch(
          changeOrderStatusAction({
            id: invoice,
            order_status_id: 2,
            reason: `Transaction ${invoice} confirmed by Admin`,
          })
        );
        toggle();
      }
    });
  };

  const cancelBtn = () => {
    if (modalName === "Order Confirmation") {
      if (status === "Pending") {
        Swal.fire({
          title: "Cancel this order?",
          text: "You won't be able to revert this",
          icon: "warning",
          showCancelButton: true,
          confirmButtonColor: "#3085d6",
          cancelButtonColor: "#d33",
          confirmButtonText: "Yes, cancel this order",
        }).then((result) => {
          if (result.isConfirmed) {
            Swal.fire({
              title: "Submit the reason of cancellation",
              input: "text",
              inputAttributes: {
                autocapitalize: "off",
              },
              inputValidator: (value) => {
                return !value && "You need to write something!";
              },
              showCancelButton: true,
              confirmButtonText: "Submit",
              showLoaderOnConfirm: true,
            }).then((results) => {
              if (results.isConfirmed) {
                Swal.fire({
                  icon: "info",
                  title: "Order Cancelled",
                  text: `${results.value}`,
                });
                dispatch(
                  changeOrderStatusAction({
                    id: invoice,
                    order_status_id: 4,
                    reason: `Transaction ${invoice} canceled: ${results.value}`,
                  })
                );
              }
            });
          }
        });
      }
    }
    toggle();
  };
  return (
    <>
      {showModal ? (
        <>
          <div className="justify-center items-center flex overflow-x-hidden overflow-y-auto fixed inset-0 z-50 outline-none focus:outline-none">
            <div
              className="relative w-auto my-6 mx-auto max-w-3xl"
              style={{ paddingTop: "35px" }}
            >
              {/*content*/}
              <div className="border-0 rounded-lg shadow-lg relative flex flex-col w-full bg-white outline-none focus:outline-none">
                {/*header*/}
                <div className="flex items-start justify-between p-5 border-b border-solid border-gray-300 rounded-t">
                  <h3 className="text-3xl font-semibold">{modalName}</h3>
                  <button
                    className="p-1 ml-auto bg-transparent border-0 text-black opacity-5 float-right text-3xl leading-none font-semibold outline-none focus:outline-none"
                    onClick={toggle}
                  >
                    <span className="text-black opacity-5 h-6 w-6 text-2xl block outline-none focus:outline-none">
                      Ã—
                    </span>
                  </button>
                </div>
                {/*body*/}
                <div className="relative p-6 flex-auto">
                  <div style={{ display: "flex", height: "270px" }}>
                    <div>
                      <Zoom>
                        <img
                          src={
                            imageUrl
                              ? `${api_url}${imageUrl}`
                              : "data:image/png;base64,"
                          }
                          alt={invoice}
                          style={{ height: "270px", width: "255px" }}
                        />
                      </Zoom>
                    </div>
                    <div style={{ paddingLeft: "10px" }}>
                      {invoice ? (
                        <div className="pb-5">
                          Invoice Number:
                          <div style={{ fontWeight: "bold" }}>{invoice}</div>
                        </div>
                      ) : null}
                      <div className="pb-5">
                        User:
                        <div className="font-bold">{name}</div>
                      </div>
                      {modalName === "Order Confirmation" ? (
                        <div>
                          {" "}
                          Status: <div className="font-bold">{status}</div>
                        </div>
                      ) : null}
                    </div>
                  </div>
                </div>
                {/*footer*/}
                <div className="flex items-center justify-end p-6 border-t border-solid border-gray-300 rounded-b">
                  <button
                    className="text-red-500 background-transparent font-bold uppercase px-6 py-2 text-sm outline-none focus:outline-none mr-1 mb-1"
                    type="button"
                    style={{ transition: "all .15s ease" }}
                    onClick={cancelBtn}
                  >
                    {modalName === "Prescription" || status !== "Pending"
                      ? "Cancel"
                      : "Cancel Order"}
                  </button>
                  {modalName === "Prescription" ? (
                    <Link
                      to={`/custom-order?recipes=${recipes_id}&userid=${user_id}&img=${imageUrl}`}
                      disabled={status === "Done"}
                    >
                      <button
                        className="bg-blue-500 text-white active:bg-green-600 font-bold uppercase text-sm px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1"
                        type="button"
                        style={{ transition: "all .15s ease" }}
                        onClick={() => toggle()}
                        disabled={status === "Done"}
                      >
                        Create Custom Order
                      </button>
                    </Link>
                  ) : (
                    <button
                      className="bg-blue-500 text-white active:bg-green-600 font-bold uppercase text-sm px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1"
                      type="button"
                      style={{ transition: "all .15s ease" }}
                      onClick={confirmBtn}
                      disabled={status !== "Pending"}
                    >
                      Confirm Order
                    </button>
                  )}
                </div>
              </div>
            </div>
          </div>
          <div className="opacity-25 fixed inset-0 z-40 bg-black"></div>
        </>
      ) : null}
    </>
  );
};

export default ImagesModal;
